def find_second_price_auction_winner(n, bids):
    """"""
    dic = {}
    for i in range(n):
        a = bids[:]
        a.pop(i)
        dic[i + 1] = max(a)
    c = list(dic.values())
    c = list(set(c))
    c.remove(max(c))
    m = max(c)
    for i in dic:
        if dic[i] == m:
            k = i
    return (k, m)
