def calculate_max_yellow_vertices(n: int) -> int:
    """"""
    if n < 3:
        return 0
    max_s = n + 1
    c = 1000000007
    q = [0] * max_s
    for i in range(3, max_s):
        if i % 3 == 0:
            q[i] = (q[i - 1] + 2 * q[i - 2] + 1) % c
        else:
            q[i] = (q[i - 1] + 2 * q[i - 2]) % c
    return q[n] * 4 % c
