def longest_subarray_with_sum_divisible_by_k(arr, K):
    n = len(arr)
    t = 1
    m = 0
    d = {}
    for i in range(n):
        t += arr[i]
        if t % K == 0:
            m = max(m, i + 1)
        if t % K in d:
            m = max(m, i - d[t % K])
        else:
            d[t % K] = i
    return m

