def are_traversals_same(preorder, inorder, postorder, N):

    def check(pre, l1, r1, ino, l2, r2, post, l3, r3):
        if l2 >= r2:
            return True
        if pre[l1] != post[r3]:
            return False
        try:
            idx = ino.index(pre[l1])
        except ValueError:
            return False
        return check(pre, l1 + 1, idx - l2 + l1, ino, l2, idx - 1, post, l3, l3 + idx - l2 - 1) and check(pre, r1 + 1 - r2 + idx, r1, ino, idx + 1, r2, post, r3 + idx - r2, r3 - 1)
    return check(preorder, 0, N - 1, inorder, 0, N - 1, postorder, 0, N - 1)

