def calculate_minimum_pump_speed(N, L, schedules):
    """"""
    if N == 0:
        return 0.0
    ma = max((u for (s, t, u) in schedules))
    K = 86400
    EPS = 1e-08

    def check(x, M=2):
        rest = L
        R = [0] * M
        for i in range(M):
            prv = 0
            for (s, t, u) in schedules:
                rest = min(rest + (s - prv) * x, L)
                rest = min(rest + (t - s) * (x - u), L)
                prv = t
                if rest < 0:
                    return 0
            rest = min(rest + (K - prv) * x, L)
            R[i] = rest
        return R[-2] - EPS < R[-1]
    left = 0
    right = ma
    while left + EPS < right:
        mid = (left + right) / 2
        if check(mid):
            right = mid
        else:
            left = mid
    return right
