def find_possible_letters(n, revealed_word, word_list):
    revealed_indices = set()
    hidden_indices = set()
    for i in range(n):
        if revealed_word[i] == '*':
            hidden_indices.add(i)
        else:
            revealed_indices.add(i)
    all_letters = set('abcdefghijklmnopqrstuvwxyz')
    revealed_letters = set(revealed_word) + {'*'}
    possible_letters = set()
    for word in word_list:
        if all((revealed_word[k] == word[k] for k in revealed_indices)):
            hidden_letters = {word[i] for i in hidden_indices}
            if not revealed_letters & hidden_letters:
                possible_letters |= hidden_letters
    valid_letters = all_letters & possible_letters
    return len(valid_letters)

