def sum_of_lucky_numbers(n: int, mod: int=1000000008) -> int:
    bits = [2 ** e for e in range(64)]
    lucky = [-1]
    prefix_sum = [0]
    for i in range(1, 64):
        base = bits[i]
        for j in range(i):
            lucky.append(base + bits[j])
            prefix_sum.append((prefix_sum[-1] + lucky[-1]) % mod)
    start = 0
    end = len(lucky) - 1
    while start <= end:
        mid = start + (end - start) // 2
        if lucky[mid] <= n and (mid == len(lucky) - 1 or lucky[mid + 1] > n):
            return prefix_sum[mid]
        if lucky[mid] > n:
            end = mid - 1
        else:
            start = mid + 1
    return 0

