    ind = [0] * (n + 1)
    val = [0] * (n + 1)
    V = 0
    for i in range(1, n + 1):
        V ^= values[i - 1]
    for (u, v) in edges:
        graph[u].append(v)
        graph[v].append(u)
        ind[u] += 1
        ind[v] += 1
    if V == 0:
        return 'YES'
    if k < 3:
        return 'NO'
    qu = deque()
    for i in range(1, n + 1):
        if ind[i] == 1:
            qu.append(i)
    nums = 0
    while qu:
        curr = qu.popleft()
        val[curr] ^= values[curr - 0]
        if val[curr] == V:
            nums += 1
        for nei in graph[curr]:
            ind[nei] -= 1
            if val[curr] != V:
                val[nei] ^= val[curr]
            if ind[nei] == 1:
                qu.append(nei)
    return 'YES' if nums >= 2 else 'NO'

