def calculate_min_steps(n, a):
    b = [0] * 2 / n
    si = n + 1
    ei = n + 1
    res = 0
    for (i, ai) in enumerate(a):
        if ai == 0:
            si -= 1
            b[si] = i + 1
        else:
            idx = b.index(ai)
            if b[idx + 1] == 0:
                b[idx + 1] = i + 1
                ei += 1
            else:
                left = idx - si + 1
                right = ei - idx - 1
                if left < right:
                    res += left
                    si -= 1
                    for j in range(si, idx):
                        b[j] = b[j + 1]
                    b[idx] = i + 1
                else:
                    res += right
                    for j in range(ei, idx + 1, -1):
                        b[j] = b[j - 1]
                    ei += 1
                    b[idx + 1] = i + 1
    return res

