def generate_abbreviated_citation(pages):
    if not pages:
        return ''
    ans = []
    start = pages[0]
    end = pages[0]
    for i in range(1, len(pages)):
        if pages[i] == end + 1:
            end = pages[i]
        else:
            if start == end:
                ans.append(str(start))
            else:
                ans.append(f'{start}-{end}')
            start = pages[i]
            end = pages[i]
    if start != end:
        ans.append(str(start))
    else:
        ans.append(f'{start}-{end}')
    return ' '.join(ans)

