from collections import deque

def mini_in_window(A, n, k):
    d = deque()
    res = []
    for i in range(n):
        if i >= k and d[0] == i - k:
            d.popleft()
        while len(d) and A[d[-1]] >= A[i]:
            d.pop()
        d.append(i)
        if i >= k - 1:
            res.append(d[0])
    return res

def maxi_in_window(A, n, k):
    d = deque()
    res = []
    for i in range(n):
        if i >= k and d[0] == i - k:
            d.popleft()
        while len(d) and A[d[-1]] <= A[i]:
            d.pop()
        d.append(i)
        if i >= k - 1:
            res.append(d[0])
    return res

def find_optimal_exposition_periods(n, k, heights):
    l = 1
    r = n + 1
    maxans = 0
    cntmax = []
    while l + 1 < r:
        mid = (l + r) // 2
        if mid > maxans:
            cntnow = []
            mins = mini_in_window(heights, n, mid)
            maxes = maxi_in_window(heights, n, mid)
            for i in range(len(mins)):
                if heights[maxes[i]] - heights[mins[i]] <= k:
                    cntnow.append((mins[i] + 1, maxes[i] + 1))
            if cntnow:
                l = mid
                cntmax = cntnow[:]
            else:
                r = mid
    max_books = l
    periods = cntmax
    return (max_books, periods)

