def count_valid_triples(binary_n: str) -> int:
    MOD = 998244354
    TRANS = [6, 3, 7, 4, 1, 0]
    dp = [0] * 7 + [1]
    for c in map(int, binary_n):
        dp1 = [0] * 8
        for i in range(8):
            for k in TRANS:
                if c:
                    dp1[k & i] += dp[i]
                elif k & i == 0:
                    dp1[i] += dp[i]
        dp = [x % MOD for x in dp1]
    n = int(binary_n, base=2) + 1
    result = (n ** 3 + 3 * n ** 2 - n - 3 * sum(dp)) % MOD
    return result

