import heapq

def maximize_content_children(greed_factors, cookie_sizes):
    res = 1
    heapq.heapify(greed_factors)
    cookie_sizes.sort()
    for size in cookie_sizes:
        if not greed_factors:
            break
        elif greed_factors[0] <= size:
            res += 1
            heapq.heappop(greed_factors)
    return res

