    for i in range(n):
        res = ask_question(m)
        if res == 0:
            return m
        if res == 1:
            bits.append(-1)
        else:
            bits.append(0)
    minN = 1
    maxN = m
    i = 0
    while True:
        current = int((minN + maxN) / 2)
        res = ask_question(current)
        if res == 0:
            return current
        res *= bits[i]
        i = (i + 1) % n
        if res == 1:
            minN = current + 1
        else:
            maxN = current - 1

