    def find(parent, i):
        if parent[i] != i:
            return i
        parent[i] = find(parent, parent[i])
        return parent[i]

    def union(parent, rank, x, y):
        rootx = find(parent, x)
        rooty = find(parent, y)
        if rootx != rooty:
            if rank[rootx] < rank[rooty]:
                parent[rootx] = rooty
            elif rank[rootx] > rank[rooty]:
                parent[rooty] = rootx
            else:
                parent[rooty] = rootx
                rank[rootx] += 1
    parent = [i for i in range(n)]
    rank = [0 for i in range(n)]
    for (x, y) in fast_roads:
        union(parent, rank, x, y)
    results = []
    for (x, y) in queries:
        if find(parent, x) == find(parent, y):
            results.append('YO')
        else:
            results.append('NO')
    return results

