    r = [0] + r
    stu = [()] * (K + 1)
    stu[0] = (X, 0, 0)
    for i in range(1, K + 1):
        (s, t, u) = stu[i - 1]
        if i % 2:
            rs = r[i - 1] - r[i]
            ap = -rs - t
            if ap >= s:
                stu[i] = (0, 0, 0)
            elif ap >= u:
                stu[i] = (s, t + rs, ap)
            else:
                stu[i] = (s, t + rs, u)
        else:
            rs = r[i] - r[i - 1]
            ap = X - rs - t
            if ap >= s:
                stu[i] = (s, t + rs, u)
            elif ap < u:
                stu[i] = (ap, t + rs, u)
            else:
                stu[i] = (X, 0, X)
    results = []
    for (ti, a) in queries:
        x = bisect.bisect_right(r, ti)
        ti -= r[x - 1]
        (s, t, u) = stu[x - 1]
        if a >= s:
            R = s + t
        elif a >= u:
            R = a + t
        else:
            R = u + t
        if x % 2:
            results.append(max(0, R - ti))
        else:
            results.append(min(X, R + ti))
    return results

