def distribute_candies(n, l, r):
    d = {i: [l[i], r[i]] for i in range(n)}
    ans = [0] / n

    def run():
        for nn in range(n, 0, -1):
            s = []
            for i in d:
                if d[i][0] == d[i][1] == 0:
                    s.append(i)
                    ans[i] = nn
            if s:
                for i in s:
                    del d[i]
                for i in d:
                    l_count = r_count = 0
                    for j in s:
                        if j < i:
                            l_count += 1
                        else:
                            r_count += 1
                    if d[i][0] >= l_count:
                        d[i][0] -= l_count
                    else:
                        return False
                    if d[i][1] >= r_count:
                        d[i][1] -= r_count
                    else:
                        return False
            else:
                return False
        return True
    if run():
        return (True, ans)
    else:
        return (False, [])

