def min_augment_operations(N, K, D, arr):
    csum = sum(arr[:K - 1])
    for x in range(K - 1, N):
        csum -= arr[x]
        if csum == 0:
            return -1
        csum -= arr[x - (K - 1)]
    (count, lastnz, pick) = (0, -1, [0] * N)
    csum = 0
    x = 0
    while x < N:
        if arr[x]:
            lastnz = x
        csum += arr[x]
        pick[x] = 1
        if x < K - 1:
            x += 1
            continue
        if pick[x - K]:
            csum -= arr[x - K]
        if csum < D:
            count += 1
            csum = 0
            pick[x] = 0
            x = lastnz + 2 * K - 1
            for y in range(lastnz + K, min(lastnz + 2 * K - 1, N)):
                if arr[y]:
                    lastnz = y
                pick[y] = 1
                csum += arr[y]
        else:
            x += 1
    return count

