    segments.sort()
    l = 1
    r = N - 1
    cnt = -1
    while l <= r:
        mid = (l + r) // 2
        seg = 1
        for i in range(M):
            if seg >= segments[i][0] or segments[i][1] > seg:
                continue
            else:
                seg = min(seg + mid, segments[i][0])
        if seg == N:
            cnt = mid
            r = mid - 1
        else:
            l = mid + 1
    return cnt

