from collections import defaultdict
from sys import setrecursionlimit
setrecursionlimit(100006)

def calculate_deployment_ways(T, test_cases):

    def dfs(x, visited, lst):
        lst.append(x)
        visited.add(x)
        for i in graph[x]:
            if i not in visited:
                dfs(i, visited, lst)
    results = []
    for case in test_cases:
        (N, M, destroyed_roads) = case
        deg = [0] * (N + 1)
        graph = defaultdict(list)
        for (u, v) in destroyed_roads:
            graph[u].append(v)
            graph[v].append(u)
            deg[u] += 1
            deg[v] += 1
        visited = set()
        (ans, cnt) = (0, 0)
        for i in range(1, N + 1):
            if i not in visited:
                lst = []
                dfs(i, visited, lst)
                l = len(lst)
                flag = 1
                for j in lst:
                    if deg[j] != l - 1:
                        flag = 0
                        break
                if flag:
                    ans += 1
                cnt += 1
        if cnt == 1:
            ans = 0
        results.append(ans)
    return results

