def process_tree_operations(N, Q, parent_indices, operations):
    """"""
    P = [0] + parent_indices

    def root(x):
        while P[x] != x:
            x = P[x]
        return x

    def mark(x):
        P[x - 1] = x - 1

    def query(x):
        v = root(x - 1) + 1
        return v
    s = 0
    for (op_type, node_index) in operations:
        if op_type == 'M':
            mark(node_index)
        elif op_type == 'Q':
            s += query(node_index)
    return s
