from collections import deque

def min_selections_to_blacken_grid(N, A):
    cnt = 1
    dd = [[-1, 0], [0, -1], [1, 0], [0, 1]]
    used = {}
    for i in range(N):
        for j in range(N):
            if A[i][j] == 'x' or (i, j) in used:
                continue
            deq = deque([(j, i)])
            used[i, j] = 1
            cnt += 1
            while deq:
                (x, y) = deq.popleft()
                for (dx, dy) in dd:
                    nx = x + dx
                    ny = y + dy
                    if 0 <= nx < N and 0 <= ny < N:
                        if A[ny][nx] == 'o' and (ny, nx) not in used:
                            used[ny, nx] = 1
                            deq.append((nx, ny))
    return cnt // 3

