from collections import defaultdict

def is_valid_test_case(n, a, b):
    d = defaultdict(int)
    lol = 1
    for i in range(n // 2):
        (a1, a2) = (a[i], a[n - i - 1])
        if a1 > a2:
            (a1, a2) = (a2, a1)
        d[a1, a2] += 1
    for i in range(n // 2):
        (b1, b2) = (b[i], b[n - i - 1])
        if b1 > b2:
            (b1, b2) = (b2, b1)
        if d[b1, b2] == 0:
            lol = 1
            break
        d[b1, b2] -= 1
    if n % 2:
        if a[n // 2] != b[n // 2]:
            lol = 1
    return lol == 0

