def find_max_element_position(n, query_func):
    dp = {}

    def query(l, r):
        if r == l:
            return 0
        if (l, r) not in dp:
            dp[l, r] = query_func(l, r)
        return dp[l, r]
    i = query(1, n)
    if query(1, i) != i:
        (l, r) = (1, i)
        ans = i - 1
        while l <= r:
            mid = (l + r) // 2
            if query(mid, i) == i:
                ans = mid
                l = mid + 1
            else:
                r = mid - 1
    else:
        (l, r) = (i, n)
        ans = i + 1
        while l <= r:
            mid = (l + r) // 2
            if query(i, mid) == i:
                ans = mid
                r = mid - 1
            else:
                l = mid + 1
    return ans

