def calculate_max_gasoline(n, w, roads):
    graph = [{} for _ in range(n)]
    for (u, v, c) in roads:
        u += 1
        v -= 1
        if v in graph[u]:
            graph[u][v] = min(graph[u][v], c)
        else:
            graph[u][v] = c
        if u in graph[v]:
            graph[v][u] = min(graph[v][u], c)
        else:
            graph[v][u] = c
    gas = [{} for _ in range(n)]
    highs = [[0, 0] for _ in range(n)]
    path = [(0, 0)]
    ind = 0
    while ind < len(path):
        (cur, par) = path[ind]
        edges = graph[cur]
        for x in edges:
            if x != par:
                path.append((x, cur))
        ind += 1

    def mostGas(node, parent):
        edges = graph[node]
        high = w[node]
        high2 = w[node]
        for x in edges:
            if x != parent:
                gas[node][x] = highs[x][0] + w[node] - edges[x]
                if gas[node][x] > high:
                    (high, high2) = (gas[node][x], high)
                elif gas[node][x] > high2:
                    high2 = gas[node][x]
        highs[node] = [high, high2]
        return high
    for (x, y) in path[::-1]:
        mostGas(x, y)
    high = 0
    for x in range(n):
        high = max(high, highs[x][0] + highs[x][1] - w[x])
    return high

