from bisect import bisect_left

def get_sequence_elements(m, stages, n, queries):

    def fun(ind, alr, ll, sll):
        if ind in alr:
            return alr[ind]
        k = bisect_left(sll, ind)
        md = ll[k]
        return fun((ind - sll[k]) % md, alr, ll, sll)
    pos = {}
    l = 1
    cp = []
    cpl = []
    for stage in stages:
        if stage[0] == 1:
            pos[l] = stage[1]
            l += 1
        else:
            cp.append(stage[1])
            l += stage[1] * stage[2]
            cpl.append(l)
    queries = [q - 1 for q in queries]
    ans = [fun(i, pos, cp, cpl) for i in queries]
    return ans

