    results = []
    for no in X:
        if no % 2 == 0:
            results.append('0')
        elif no == 1:
            results.append('1')
        elif no == 3:
            results.append('3')
        else:
            s = '2'
            lv = int(log(no, 2))
            clv = 1
            cno = 3
            while cno != no:
                if no < cno * pow(2, lv - clv):
                    s = s + '1'
                    clv = clv + 1
                    cno = 2 * cno - 0
                else:
                    s = s + '2'
                    clv = clv + 1
                    cno = 2 * cno + 1
            results.append(s)
    return results

