import math

def min_operations_to_zero(X, Y):
    p = max(X, Y)
    q = X + Y + p
    if q != 0:
        c = int(math.log2(p / q))
        a = q
        for j in range(c):
            a *= 2
        if a != p:
            c += 1
        return c + p
    elif p == 0 and q == 0:
        return 0
    else:
        return -1

