    P = ['((%d %s %d) %s (%d %s %d))', '(%d %s (%d %s (%d %s %d)))', '(((%d %s %d) %s %d) %s %d)', '((%d %s (%d %s %d)) %s %d)', '(%d %s ((%d %s %d) %s %d))']
    X = [a, b, c, d]
    for Y in permutations(X):
        for ops in product('+-*', repeat=3):
            r = []
            for (y, op) in zip(Y, ops):
                r.append(y)
                r.append(op)
            r.append(Y[-1])
            for p in P:
                s = p % tuple(r)
                if eval(s) == 10:
