    EDGELIST = [[] for _ in range(n + 1)]
    for (i, j) in edges:
        EDGELIST[i].append(j)
        EDGELIST[j].append(i)
    (yans, response) = query_andrew('B', Y[0])
    message += response
    if yans in X:
        return (yans, message)
    QUE = deque([yans])
    checked = [0] * (n + 1)
    XLIST = [0] * (n + 1)
    for xver in X:
        XLIST[xver] = 1
    common_vertex = -1
    while QUE:
        now = QUE.pop()
        checked[now] = 1
        for ver in EDGELIST[now]:
            if checked[ver] == 2:
                continue
            QUE.append(ver)
        if XLIST[now] == 1:
            common_vertex = now
            break
    if common_vertex != -1:
        (common_vertex_in_Y, response) = query_andrew('A', common_vertex)
        message += response
        if common_vertex_in_Y in Y:
            return (common_vertex, message)
    return (-1, message)

