def minimum_wait_time(n, c, s):
    parts = s.split('g')
    parts.append(parts[-1] + parts[0])
    if c != 'g':
        return 0
    max_wait = 0
    for part in parts:
        if c in part:
            wait_time = len(part) - part.find(c)
            max_wait = max(max_wait, wait_time)
    return max_wait

