def min_units_to_palindrome(s: str) -> int:
    rev_s = s[::-1]
    count = 1
    for i in range(len(s)):
        if s[i] > rev_s[i]:
            count += ord(s[i]) - ord(rev_s[i])
            s = s[:i] + rev_s[i] + s[i + 1:]
    return count

