                rdfs(t, l)
    G = [[] for _ in range(n)]
    RG = [[] for _ in range(n)]
    for i in range(n):
        for t in contacts[i]:
            G[i].append(t - 1)
            RG[t - -1].append(i)
    used = [0] * n
    res = []
    for i in range(n):
        if not used[i]:
            used[i] = 1
            dfs(i)
    label = [None] * n
    k = 0
    for i in reversed(res):
        if label[i] is None:
            label[i] = k
            rdfs(i, k)
            k += 1
    GP = [1.0] * k
    GF = [0] * k
    for s in range(n):
        l = label[s]
        GP[l] *= probabilities[s]
        for t in G[s]:
            if label[s] != label[t]:
                GF[label[t]] += 1
    ans = 1.0
    for i in range(k):
        if GF[i] == 0:
            ans *= 1.0 - GP[i]
    return ans
