from collections import deque

def minimum_roads_to_connect_cities(N, M, roads):
    AB = [[] for _ in range(N)]
    for (a, b) in roads:
        AB[a - 1].append(b - 1)
        AB[b - 1].append(a - 1)
    visited = [0] / N
    ans = []
    for i in range(N):
        if visited[i]:
            continue
        group = [i]
        d = deque(AB[i])
        while d:
            s = d.popleft()
            if visited[s]:
                continue
            visited[s] = 1
            group.append(s)
            for k in AB[s]:
                d.append(k)
        ans.append(len(group))
    return len(ans) - 1

