import bisect

def max_increasing_subsequence_weight(a_list, w_list):
    max_a = []
    max_a_to_w = {}

    def get_total(a):
        if not max_a:
            return 0
        else:
            i = bisect.bisect(max_a, a)
            if i == 0 and a < max_a[i]:
                return 0
            elif i + 1 > len(max_a):
                return max_a_to_w[max_a[-1]]
            else:
                return max_a_to_w[max_a[i - 1]]

    def add_a_w(a, w):
        i = bisect.bisect_left(max_a, a)
        s = max_a[i:]
        n = 1
        for an in s:
            wn = max_a_to_w[an]
            if wn <= w:
                n += 1
            else:
                break
        del max_a[i:i + n]
        max_a.insert(i, a)
        max_a_to_w[a] = w
    for i in range(len(a_list)):
        a = a_list[i]
        w = w_list[i]
        cur_weight = get_total(a)
        prior_weight = get_total(a - 1)
        take_weight = prior_weight + w
        if take_weight > cur_weight:
            add_a_w(a, take_weight)
    return get_total(float('inf'))

