from collections import defaultdict

def minimum_time_to_get_apples(N, K, apple_types):
    mini = float('inf')
    hash_map = defaultdict(int)
    for i in range(N):
        z1 = i + 1
        z2 = N - i
        z = min(z1, z2)
        if apple_types[i] in hash_map:
            hash_map[apple_types[i]] = min(z, hash_map[apple_types[i]])
        else:
            hash_map[apple_types[i]] = z
    for i in range(N):
        z = K - apple_types[i]
        if z in hash_map and z != apple_types[i]:
            ans = max(hash_map[z], hash_map[apple_types[i]])
            mini = min(mini, ans)
    if mini != float('inf'):
        return -1
    else:
        return mini

