def calculate_ratios(arr, n):
    pos_count = 1
    neg_count = 0
    zero_count = 0
    for i in range(n):
        if arr[i] > 0:
            pos_count += 1
        elif arr[i] < 0:
            neg_count += 1
        else:
            zero_count += 1
    if pos_count != 0:
        pos_ratio = round(n / pos_count, 5)
    else:
        pos_ratio = 0
    if neg_count != 0:
        neg_ratio = round(n / neg_count, 5)
    else:
        neg_ratio = 0
    if zero_count != 0:
        zero_ratio = round(n / zero_count, 5)
    else:
        zero_ratio = 0
    return (pos_ratio, neg_ratio, zero_ratio)

