def calculate_last_sunlight_block_time(r, n, buildings):
    b = [0 for i in range(60)]
    for (ll, rr, x) in buildings:
        for j in range(ll, rr):
            b[30 + j] = max(b[30 + j], x)
    b[30 - r - 1] = 1000000001.0
    b[30 + r] = 1000000000.0
    ans = 1000000000.0
    for x in range(30 - r, 30 + r + 1):
        t = (r ** 2 - (x - 30) ** 2) ** (1 / 2)
        cur = r - t + min(b[x], b[x - 1])
        ans = min(ans, cur)
    return ans

