def calculate_xor_of_binary_strings(binary_str1: str, binary_str2: str) -> str:
    """"""
    result = []
    for i in range(len(binary_str1)):
        if binary_str1[i] != binary_str2[i]:
            result.append('1')
        else:
            result.append('0')
    return ''.join(result)
