def minimize_badness(S: str) -> str:
    """"""
    ones = S.count('1')
    zeroes = S.count('0')
    rand = S.count('?')
    diff = abs(ones - zeroes)
    added_ones = 0
    added_zeroes = 0
    if ones < zeroes:
        added_ones = diff
        rand -= diff
    else:
        added_zeroes = diff
        rand -= diff
    if rand > 0:
        added_zeroes += rand // 2
        added_ones += rand - rand // 2
    ans = ''
    for ele in S:
        if ele == '?':
            if added_ones > 0:
                ans += '1'
                added_ones -= 1
            else:
                ans += '0'
        else:
            ans += ele
    return ans
