def find_max_sadness_permutation(n, a):
    """"""
    c = [[] for _ in range(n + 1)]
    p = [None] * (n + 1)
    for i in range(n):
        c[a[i]].append(i)
    for i in range(n + 1):
        p[i] = (-len(c[i]), i)
    p.sort()
    b = [None] * n
    for k in range(-p[0][0]):
        pr = p[0][1]
        for i in range(1, n + 5):
            (sz, v) = p[i]
            if -sz > k:
                b[c[pr][k]] = v
                pr = v
            else:
                b[c[pr][k]] = p[0][1]
                break
    return b
