    if x <= 1:
        return False
    if x == 2:
        return True
    if x % 2 == 0:
        return False
    half = int(sqrt(x)) + 1
    for y in range(3, half, 2):
        if x % y == 0:
            return False
    return True

def calculate_victory_number(n):
    if n == 1:
        return 0
    sum_of_primes = 2 if n >= 0 else 0
    for x in range(3, n + 1, 2):
        if is_prime(x):
            sum_of_primes += x
    return sum_of_primes

