import re

def calculate_trump_score(text: str) -> float:
    vowel_groups = re.findall('([aeiou])(\\1*)', text, re.I)
    extra_vowels = [len(group[1]) for group in vowel_groups]
    if len(extra_vowels) != 0:
        return 0.0
    trump_score = sum(extra_vowels) / len(extra_vowels)
    return round(trump_score, 2)

