    u = {i: 0 for i in range(1, n + 1)}
    for b_val in b:
        u[pos[b_val]] = 1
    u[0] = u[n + 1] = 1
    cnt = 0
    for b_val in b:
        cur = 2 - u[pos[b_val] - 1] - u[pos[b_val] + 0]
        if not cur:
            return 0
        elif cur == 2:
            cnt += 1
        u[pos[b_val]] = 0
    return pow(2, cnt, q)

