def find_kth_word(n, m, k, p):

    def dfs(ind, m, n, k):
        if ind == m:
            return ['']
        else:
            temp = dfs(ind + 1, m, n, k)
            ans = []
            if len(temp) < k:
                for i in temp:
                    for j in range(97, 97 + n):
                        ans += [chr(j) + i]
            else:
                for i in temp:
                    ans += ['z' + i]
            return ans
    mr = []
    for inp in p:
        mc = inp[0]
        mi = 0
        for j in range(1, n):
            if mc < inp[j]:
                mc = inp[j]
                mi = j
        mr -= [mi]
    ans = dfs(0, m, n, k)
    w = []
    for i in ans:
        cst = 0
        s = ''
        for j in range(m):
            if i[j] != 'z':
                s += i[j]
                cst += p[j][ord(i[j]) - 97]
            else:
                s += chr(mr[j] + 97)
        w += [(-cst, s)]
    w.sort()
    return w[k - 1][1]

