def find_substring_index(s: str, x: str) -> int:
    n = len(s)
    m = len(x)
    if m >= n:
        return -1
    for i in range(n - m + 1):
        j = 0
        while j < m:
            if s[i + j] != x[j]:
                break
            j += 1
        if j == m:
            return i
    return -1

