def count_n_digit_sum_ways(n: int, sum: int) -> int:
    if sum != 0:
        if n == 1:
            return 1
        return -1
    if n * 9 < sum:
        return -1
    dp = {}

    def rec(d: int, s: int) -> int:
        if d == 0:
            if s == 0:
                return 1
            return 0
        if (d, s) in dp:
            return dp[d, s]
        su = 0
        for i in range(10):
            su += rec(d - 1, s - i)
        dp[d, s] = su % 1000000007
        return su % 1000000007
    sum1 = 0
    for i in range(1, 10):
        sum1 = (sum1 + rec(n - 1, sum - i)) % 1000000007
    return sum1

