def select_vertices(n, m, edges):
    E = [[] for _ in range(n)]
    for (a, b) in edges:
        E[a - 1].append(b - 1)
        E[b - 1].append(a - 1)
    D = [-1] / n
    D[0] = 0
    d = 0
    post = [0]
    EVEN = [1]
    ODD = []
    while post:
        d += 1
        pre = post
        post = []
        for i in pre:
            for e in E[i]:
                if D[e] < 0:
                    D[e] = d
                    post.append(e)
                    if d % 2:
                        ODD.append(e + 1)
                    else:
                        EVEN.append(e + 1)
    if len(ODD) < len(EVEN):
        chosen_vertices = ODD
    else:
        chosen_vertices = EVEN
    return (len(chosen_vertices), chosen_vertices)

