def calculate_vertex_counters(N, Q, edges, operations):
    import collections
    d = collections.defaultdict(list)
    score = [0] / N
    for (a, b) in edges:
        d[a].append(b)
    for (p, x) in operations:
        score[p - 1] += x
    for node in range(1, N + 1):
        for child in d[node]:
            score[child - 1] += score[node - 1]
    return score

