def minimize_max_break(n, k, availability):

    def is_feasible(hop):
        at = 0
        used = 0
        while used < k - 2 and at + hop < n:
            to = g[at + hop]
            if to == at:
                break
            used += 1
            at = to
        if n - 1 - at > hop:
            return False
        return True
    g = [0] / n
    for i in range(n):
        if availability[i] == '0':
            g[i] = i
        else:
            g[i] = g[i - 1] if i > 0 else -1
    (lo, hi) = (-1, n + 1)
    while hi - lo > 1:
        mid = (lo + hi) // 2
        if is_feasible(mid):
            hi = mid
        else:
            lo = mid
    return hi - 1

